import './App.css';
import axios from 'axios' //Library that allows you to make requests to an API
import {useState} from 'react'

function App() {

  const api = {key: "7789e24a2f20807676e14f669853b082", url: "https://api.openweathermap.org/data/2.5/"}

  const [data, setData] = useState({})
  const [location, setLocation] = useState('');

  function searchLocation(event){
    if(event.key === "Enter"){
      axios.get(`${api.url}weather?q=${location}&units=metric&APPID=${api.key}`).then((result) => {
        setData(result.data)
        console.log(result.data)
      })
    }
  }

  return (
    <div className="App">
      <div className='searchBoxContainer'>
        <input value={location} type="text" placeholder='Enter Location' onChange={(event) => {setLocation(event.target.value);}} onKeyPress={searchLocation}/>
      </div>
      <div className='LocationContainer'>
        <p>{data.name}</p>
      </div>
      <div className='WeatherConditionsContainer'>
        {data.weather ? <p>{data.weather[0].main} - {data.weather[0].description}</p> : null}
      </div>
      <div className='TempContainer'>
        {data.main ? <p>{data.main.temp} °C</p> : null}
      </div>
      <div className='TempFeelsLikeContainer'>
        {data.main ? <p>{data.main.feels_like} °C</p> : null}
      </div>
      <div className='HumidityContainer'>
        {data.main ? <p>{data.main.humidity} %</p> : null}
      </div>
      <div className='PressureContainer'>
        {data.main ? <p>{data.main.pressure} mb</p> : null}
      </div>
      <div className='WindSpeedContainer'>
        {data.wind ? <p>{data.wind.speed} mph</p> : null}
      </div>
    </div>
  );
}

export default App;
